<!-- Code By Kittipol Kaewhem -->   <link rel="stylesheet" href="resource/bootstrap/css/bootstrap.min.css"><style type="text/css">	body,html{		margin: 0;		height: 100%;	}	#content{		height: 100%;		position: relative;	}	#content .bg_content{		position: absolute;		top: 0;		left: 0;		display: block;		width: 100%;		height: 100%;		background-image: url("view/page/images/bg.jpg");		background-attachment: fixed;		background-repeat: no-repeat;		background-size: cover;		background-position: center center;		z-index: -1;	}	#content .wrap-content{		width: 950px;		position: relative;		margin: 0 auto;		height: 100%;	}	#content .wrap-content .block-login{		display: block;		width: 410px;		height: 420px;		background-color: #fff;		position: absolute;		top: 50%;		left: 50%;		margin-top: -210px;		margin-left: -205px;		border-radius: 4px;		text-align: center;		-webkit-box-shadow: 0px 0px 13px 0px rgba(0,0,0,0.53);		-moz-box-shadow: 0px 0px 13px 0px rgba(0,0,0,0.53);		box-shadow: 0px 0px 13px 0px rgba(0,0,0,0.53);	}	#content .wrap-content .block-login .bg-top{		display: inline-block;		width: 100%;		margin-bottom: 26px;	}	#content .wrap-content .block-login .text{		font-size: 17px;		line-height: 17px;		color: #464646;		margin-bottom: 28px;	}	#content .wrap-content .block-login .input-text{		display: block;		width: 300px;		height: 30px;		border: 1px solid #9c9c9c;		font-size: 14px;		left: 30px;		color: #464646;		margin-left: auto;		margin-right: auto;		margin-bottom: 16px;		border-radius: 3px;		padding-left: 8px;		padding-right: 8px;	}	#content .wrap-content .block-login .btn-submit{		background-color: #3c8dbc;		color:#fff;		font-size: 14px;		line-height: 30px;		width: 80px;		height: 30px;		border-radius: 3px;		text-align: center;		margin: 30px auto 0;		cursor: pointer;	}	#content .wrap-content .block-login .btn-submit:hover{		-webkit-box-shadow: inset 0px 0px 5px 0px rgba(0,0,0,0.53);		-moz-box-shadow: inset 0px 0px 5px 0px rgba(0,0,0,0.53);		box-shadow: inset 0px 0px 5px 0px rgba(0,0,0,0.53);		text-shadow: 0px 0px 5px rgba(0,0,0,0.53);	}	#content .wrap-content .block-login .part{		color: #ff1919;		font-weight: bold;		font-size: 17px;		line-height: 17px;		text-align: right;		padding-right: 26px;	}</style><html><head>	<title>login r1</title></head><body>	<div id="content" class = "col-md-12">		<div class="bg_content"></div>        		<div class="wrap-content container">			<div class="block-login col-md-6">				<div id="error">			        <!-- error will be shown here ! -->			        </div>				<img class="bg-top" src="view/page/images/top.png">								<form name = "login-form" id = "login-form" method = "post">				<div class="text">เข้าสู่ระบบ..</div>				<input placeholder="Username" id="username" name="username" class="input-text" type="text"/>				<input placeholder="Password" id="password" name="password" class="input-text" type="password"/>				<div class="form-group">			            <button type="submit" class="btn btn-default" name="btn-login" id="btn-login">			      <span class="glyphicon glyphicon-log-in"></span> &nbsp; Sign In			   </button> 			        </div>  				</form>			</div>		</div>	</div></body></html><script src="plugins/jQuery/jquery-2.2.3.min.js"></script><script type="text/javascript">	 $('#btn-login').on('click', function(e) { 		var data = $("#login-form").serialize();    			   $.ajax({			    			   type : 'POST',			   url  : 'verify.php',			   data : data,			   beforeSend: function()			   { 			    $("#error").fadeOut();			    $("#btn-login").html('<span class="glyphicon glyphicon-transfer"></span> &nbsp; sending ...');			   },			   success :  function(response)			      {      			     if(response=="ok"){			         			      $("#btn-login").html('<img src="btn-ajax-loader.gif" /> &nbsp; Signing In ...');			      setTimeout(' window.location.href = "home.php"; ',4000);			     }			     else{			         			      $("#error").fadeIn(1000, function(){      			    $("#error").html('<div class="alert alert-danger"> <span class="glyphicon glyphicon-info-sign"></span> &nbsp; '+response+' !</div>');			           $("#btn-login").html('<span class="glyphicon glyphicon-log-in"></span> &nbsp; Sign In');			         });			     }			     }			   });    		return false;    })</script>